func TestShortestPathRemoveObstacles(t *testing.T) {
	assert := assert.New(t)

	a := make([][]int, 40)
	for i := range a {
		a[i] = make([]int, 40)
	}
	Results = append(Results, Result{a, 0, 78})

	for _, res := range Results {
		want := res.Expected
		got := shortestPath(res.Input, res.K)
		assert.Equal(want, got, fmt.Sprintf("%+v", res))
	}
}
